import React from 'react';
import Prism from 'prismjs';
import 'prismjs/themes/prism.css';  // Import the PrismJS theme for syntax highlighting
import './CodeBlock.css';  // Custom CSS for the component

function CodeBlock({ code }) {
    React.useEffect(() => {
        Prism.highlightAll();  // Highlights the code after rendering
    }, [code]);

    const handleCopy = () => {
        navigator.clipboard.writeText(code);
        alert("Code copied to clipboard!");
    };

    return (
        <div className="code-block-container">
            <button onClick={handleCopy} className="copy-button">Copy</button>
            <pre className="code-block">
                <code>{code}</code>  {/* Render the code without language keyword */}
            </pre>
        </div>
    );
}

export default CodeBlock;
