import React from 'react';
import Prism from 'prismjs';
import 'prismjs/themes/prism.css';  // PrismJS theme

function CodeBlock({ code }) {
    React.useEffect(() => {
        Prism.highlightAll();
    }, [code]);

    return (
        <pre className="code-block">
            <code className="language-python">
                {code}
            </code>
        </pre>
    );
}

export default CodeBlock;
