import React from 'react';
import './CodeBlock.css';

function CodeBlock({ code }) {
    return (
        <div className="code-block">
            <pre>
                <code>{code}</code>
            </pre>
            <button
                className="copy-button"
                onClick={() => navigator.clipboard.writeText(code)}
            >
                Copy to Clipboard
            </button>
        </div>
    );
}

export default CodeBlock;
