import React, { useState, useEffect } from 'react';
import axios from 'axios';
import CodeBlock from './CodeBlock';
import ChatHistory from './ChatHistory';
import './ChatBox.css';

const BACKEND_URL = process.env.REACT_APP_BACKEND_URL;
console.log("Backend URL:", BACKEND_URL);

function ChatBox() {
    const [input, setInput] = useState('');
    const [responseText, setResponseText] = useState('');
    const [generatedCode, setGeneratedCode] = useState('');
    const [loading, setLoading] = useState(false);

    // Function to handle generating the code
    const handleGenerate = async () => {
        setLoading(true);
        console.log('Generating code for input:', input); // Log input to ensure it's captured

        try {
            const response = await axios.post(`${BACKEND_URL}/api/generate`, { input });
            console.log('API Response:', response); // Log the entire response

            if (response.status === 200) {
                const { text, code } = response.data.response;
                console.log('Generated Text:', text);  // Log generated text
                console.log('Generated Code:', code);  // Log generated code
                setResponseText(text);
                setGeneratedCode(code);
            } else {
                console.error('Non-200 response:', response);
            }
        } catch (error) {
            console.error('Error generating code:', error); // Log error details
            setResponseText("Error generating response. Please try again.");
        }
        setLoading(false);
    };

    // Automatically trigger handleGenerate when the user presses "Enter"
    const handleKeyDown = (event) => {
        if (event.key === 'Enter') {
            event.preventDefault();
            if (!loading && input.trim() !== '') {
                handleGenerate();
            }
        }
    };
    return (
        <div className="chat-container">
            <div className="chat-history">
                <ChatHistory />
            </div>
            <div className="chat-box">
                <textarea
                    value={input}
                    onChange={(e) => setInput(e.target.value)}
                    onKeyDown={handleKeyDown}
                    placeholder="Enter your instructions here"
                />
                <button onClick={handleGenerate} disabled={loading}>
                    {loading ? 'Generating...' : 'Generate'}
                </button>

                <div className="response-text">
                    <h3>Response</h3>
                    <p>{responseText}</p>
                </div>

                <div className="code-section">
                    <CodeBlock code={generatedCode} />
                </div>
            </div>
        </div>
    );
}

export default ChatBox;
