import React, { useState } from 'react';
import axios from 'axios';
import CodeBlock from './CodeBlock';
import './ChatBox.css';

const BACKEND_URL = process.env.REACT_APP_BACKEND_URL;
console.log("Backend URL:", BACKEND_URL);

function ChatBox() {
    const [input, setInput] = useState('');
    const [textBefore, setTextBefore] = useState('');
    const [generatedCode, setGeneratedCode] = useState('');
    const [textAfter, setTextAfter] = useState('');
    const [loading, setLoading] = useState(false);

    const handleGenerate = async () => {
        setLoading(true);
        try {
            const response = await axios.post(`${BACKEND_URL}/api/generate`, { input });
            const { text_before, code, text_after } = response.data.response;
            setTextBefore(text_before);
            setGeneratedCode(code);
            setTextAfter(text_after);
        } catch (error) {
            console.error('Error generating code:', error);
            setTextBefore("Error generating response. Please try again.");
        }
        setLoading(false);
    };

    return (
        <div className="chat-container">
            <div className="chat-box">
                <textarea
                    value={input}
                    onChange={(e) => setInput(e.target.value)}
                    placeholder="Enter your instructions here"
                />
                <button onClick={handleGenerate} disabled={loading}>
                    {loading ? 'Generating...' : 'Generate'}
                </button>

                <div className="response-section">
                    <p>{textBefore}</p>
                    {generatedCode && <CodeBlock code={generatedCode} />}
                    <p>{textAfter}</p>
                </div>
            </div>
        </div>
    );
}

export default ChatBox;
